<template>
  <div class="general">
    <div class="center">
      <section id="content">
        <h1 class="subheader">Últimas películas</h1>
        <div class="favorita" v-if="favorita">
          La película marcada es:
          <h3>{{ favorita.title }}</h3>
        </div>
        <!--Listado articulos-->
        <div id="articles">
          <div
            v-for="pelicula in peliculasMayuscula"
            v-bind:key="pelicula.title"
          >
            <PeliculaComponent
              :pelicula="pelicula"
              v-on:favorita="haLlegadoLaPelicula"
            ></PeliculaComponent>
          </div>
        </div>
      </section>
      <SidebarComponent></SidebarComponent>
      <div class="clearfix"></div>
    </div>
  </div>
</template>
<script>
import PeliculaComponent from "./PeliculaComponent.vue";
import SidebarComponent from "./SidebarComponent.vue";
export default {
  name: "PeliculasComponent",
  data() {
    return {
      favorita: null,
      nombre: 'Sol',
      apellido: 'Galain',
      web: 'solgalain.com',
      peliculas: [
        {
          title: "Batman",
          year: 2016,
          image:
            "data:image/jpeg;base64,",
        },
        {
          title: "La mujer maravilla",
          year: 2018,
          image:
            "data:image/jpeg;base64,",
        },
      ],
    };
  },
  components: {
    PeliculaComponent,
    SidebarComponent,
  },
  computed: {
    peliculasMayuscula() {
      var peliculasMod = this.peliculas;

      for (var i = 0; i < this.peliculas.length; i++) {
        peliculasMod[i].title = peliculasMod[i].title.toUpperCase();
      }

      return peliculasMod;
    },
  },
  methods: {
    haLlegadoLaPelicula(favorita) {
      this.favorita = favorita;
      // console.log(favorita);
    },
  },
};
</script>